import {useState} from "react";

const FormAdd = () =>{

    //States for Values - Props
    const [name, setName] =useState("");
    const [move, setMove] =useState("");
    const [skill, setSkill] =useState("");
    const [anime, setAnime] =useState("");
    //States for error handling
    const [errorName, setErrorName] = useState("");
    const [errorMove, setErrorMove] = useState("");
    const [errorSkill, setErrorSkill] = useState("");
    const [errorAnime, setErrorAnime] = useState("");



    const nameHandler =(e)=>{
        if(name.length <= 2)
        {
            setErrorName("CHARACTER SHOULD HAVE MORE THAN 2 LETTERS!");
        }
        else
        {
            setErrorName("");
        }
        setName(e.target.value);
    }

    // const skillValidator=()=>{
    //    if(skill<=10)
    //    {
    //     setErrorMove("MOVE SHOULD BE MOER THAN 5!");
    //    }
    //    else
    //    {
    //     return ("");
    //    }
    // }

    const moveHandler=(e)=>{
        if(move.length<=5)
        {
            setErrorMove("MOVE SHOULD BE MOER THAN 5!");
        }
        else
        {
            setErrorMove("");
        }
        setMove(e.target.value);
     }



    return (
    <div>
        <form>  
        <h2>CREATE AN ANIME CHARACTER</h2>
        <div>
          <label htmlFor="name">ENTER CHARACTER NAME:</label>
          <input type="text" id="name" name="name" onChange={(e)=>{nameHandler(e)}}
          value={name}></input>
        </div>
        {/* {(name.length <= 2)? <p>NAME SHOULD BE MORE THAN 2!</p>:null} */}
        {errorName}

        <div>
          <label htmlFor="move">ENTER FAVORITE MOVE:</label>
          <input type="text" id="move" name="move" onChange={(e)=>{moveHandler(e)}}
          value={move}></input>
        </div>
        {/* {move.length <=5 && <p>MOVE SHOULD BE MOER THAN 5!</p>} */}
        {errorMove}


        <div>
          <label htmlFor="skill">ENTER SKILL LEVEL:</label>
          <input type="number" id="skill" name="skill" onChange={(e)=>{setSkill(e.target.value)}}
          value={skill}></input>
        </div>
    

        <div>
          <label htmlFor="skill">SELECT ANIME:</label>
          <select id="anime" name="anime" onChange={(e)=>{setAnime(e.target.value)}}
          value={anime}>
              <option value="">---------</option>
              <option value="NARUTO">NARUTO</option>
              <option value="ONE PIECE">ONE PIECE</option>
              <option value="ATTACK ON TITANS">ATTACK ON TITANS</option>
              <option value="BLACK COVER">BLACK CLOVER</option>
          </select>
        
        </div>
      </form>
        <hr/>
        <h2>DISPLAY VALUE</h2>
        <div>
            <label htmlFor="">FROM: {anime}</label>
        </div>
        <div>
            <label htmlFor="">CHARACTER NAME:{name}</label>
        </div>
        <div>
            <label htmlFor="">CHARACTER MOVE:{move}</label>
        </div>
        <div>
            <label htmlFor="">CHARACTER SKILL:{skill}</label>
        </div>
      </div>
    );
}
export default FormAdd;
