import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

const TheForm =(props)=>{


    const [anime,setanime]=useState(
        props.oldAnime? props.oldAnime: {
        characterName:'',
        animeTitle:'',
        powerLevel:0,
    },);
    
    const [errors,setErrors]=useState({});

    useEffect()

    const handleChange =(e)=>{
        setanime({...anime, [e.target.name]:e.target.value});
    }

    const submitHandler =(e)=>{
        e.preventDefault();
        props.submitHandler(anime,setErrors);
    }
    return(
        <form onSubmit={(e)=>{submitHandler(e)}}>
        <div>
            <label>CHARACTER NAME:</label>
            <input type="text" value={anime.characterName} name="characterName" onChange={handleChange}/>
            <br/>
            {errors.characterName && (<span> {errors.characterName.message} </span>)}
        </div>
        <div>
            <label>ANIME TITLE:</label>
            <input type="text" value={anime.animeTitle} name="animeTitle" onChange={handleChange}/>
            <br/>
            {errors.animeTitle && (<span> {errors.animeTitle.message} </span>)}
        </div>
        <div>
            <label>POWER LEVEL:</label>
            <input type="number" value={anime.powerLevel}  name="powerLevel" onChange={handleChange}/>
            <br/>
            {errors.powerLevel && (<span> {errors.powerLevel.message} </span>)}
        </div>
        <button type="submit">{props.buttonText}</button>
    </form>
    )
}

export default TheForm;
