import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const CreateNew =(props)=>{

    const [characterName, setcharacterName] = useState("");
    const [animeTitle, setanimeTitle] = useState("");
    const [powerLevel, setpowerLevel] = useState(0);
    
    const handleSubmit =(e)=>{
        e.preventDefault();
        axios.post("http://localhost:8000/api/newAnime",
        {
            characterName,animeTitle,powerLevel
        })
        .then((res)=>{
            console.log(res.data)
            navigate("/");
        })
        .finally((err)=>{console.log(err)})
         
    }

 return(
     <>
        <p>Create New Character!</p>
        <hr/>
        <form onSubmit={(e)=>{handleSubmit(e)}}>
            <div>
                <label>CHARACTER NAME:</label>
                <input type="text" onChange={(e)=>{setcharacterName(e.target.value)}}/>
            </div>
            <div>
                <label>ANIME TITLE:</label>
                <input type="text" onChange={(e)=>{setanimeTitle(e.target.value)}}/>
            </div>
            <div>
                <label>POWER LEVEL:</label>
                <input type="number" onChange={(e)=>{setpowerLevel(e.target.value)}}/>
            </div>
            <button>ADD NEW</button>
            

        </form>
     </>
        )
}
export default CreateNew;
