import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams, useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import TheForm from "./TheForm";


const UpdateOne =(props)=>
{
    const navigate = useNavigate();
    const {aid} = useParams();
    const {state} = useLocation();  
    const [oldAnime, setOldAnime] = useState({});

    useEffect(()=>{
   
            console.log(state);
            axios.get("http://localhost:8000/api/showOne/"+aid)
            .then((res)=>{
                console.log(res);
                console.log(res.data);
                setOldAnime(res.data);
            })
            .catch((err)=>{
                console.log(err)
            })
          
    },[])

    const submitHandler=(anime,setErrors)=>{
        axios.put("http://localhost:8000/api/updateAnime/"+aid,anime)
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            navigate("/home/");
        })
        .catch((err)=>{
            console.log(err);
            setErrors(err.response.data.errors);
        })
    }

    return oldAnime?(
       <TheForm submitHandler={submitHandler} buttonText={"UPDATE CHARACTER"} oldAnime={oldAnime} />
    ):null;
}

export default UpdateOne;
