const TheForm2 =(props)=>{

    return (
    <form onSubmit={(e)=>{submitHandler(e)}}>
    <div>
        <label>CHARACTER NAME:</label>
        <input type="text" name="characterName" value={characterName} onChange={newchangeHandler}/>
        <br/>
        {errors.characterName && (<span> {errors.characterName.message} </span>)}
    </div>
    <div>
        <label>ANIME TITLE:</label>
        <input type="text" name="animeTitle" value={animeTitle} onChange={newchangeHandler}/>
        <br/>
        {errors.animeTitle && (<span> {errors.animeTitle.message} </span>)}
    </div>
    <div>
        <label>POWER LEVEL:</label>
        <input type="number" name="powerLevel" value={powerLevel} onChange={newchangeHandler}/>
        <br/>
        {errors.powerLevel && (<span> {errors.powerLevel.message} </span>)}
    </div>
    <button type="submit">ADD NEW CHARACTER</button>
    </form>
    )
}

export default TheForm2;
