import React,{useState, useEffect} from "react";
import axios from 'axios';  
const ShowFilms =(props)=>{

    const link=props;
    const {moviedata,setMovieData}= useState({});
    console.log("From show: "+link);
    useEffect(()=>{
        axios.get(`${link}`)
        .then((res)=>{
          //console.log("From show: "+res.data);
          setMovieData(res.data);
        })
        .catch((err)=>{
          console.log(err)
        })
      },[link])

    return(
        <>
            <p>TITLE:{moviedata.tile}</p>
            <p>TITLE:{moviedata.episode_id}</p>
        </>
    )
}
export default ShowFilms;
