    setCharacter(res.data);
    //console.log(res.data.films);
    setTheFilms(res.data.films);

  })
  .catch((err)=>{
    console.log(err);
  })
},[active])

const incre=()=>{
 setActive(active+1);
}
const decre=()=>{
setActive(active-1);
}

  return (
    <div className="App">
     <Characters theCharacter={character}></Characters>
      <button onClick={()=>{decre()}}>PREVIOUS</button>
      <button onClick={()=>{incre()}}>NEXT</button>
      <hr/>
      <h1>FILMS:</h1>
      {theFilms.map((elems, index)=>{
        return (<Films listofFilms={theFilms}></Films>)
      })}

    </div>
  );
}

export default App;
