import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams, useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import TheForm from "./TheForm";


const UpdateOne =(props)=>
{
    const navigate = useNavigate();
    const {aid} = useParams();
    const {state} = useLocation();  

    const [oldAnime, setOldAnime] = useState({});

    const [characterName,setcharacterName]= useState("");
    const [animeTitle,setanimeTitle]= useState("");
    const [powerLevel,setpowerLevel]= useState(0);

    useEffect(()=>{
        console.log(anime);
        if(!state)
        {
            axios.get("http://localhost:8000/api/showOne/"+aid)
            .then((res)=>{
                console.log(res);
                console.log(res.data);
                setOldAnime(res.data);
            })
            .catch((err)=>{
                console.log(err)
            })
        }
        else
        {
            setOldAnime(state); 
        }
       
    },[aid])

    const submitHandler=(anime,setError)=>{
        axios.put("http://localhost:8000/api/updateAnime/"+aid,anime)
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            navigate("/home/");
        })
        .catch((err)=>{
            console.log(err);
            setError(err.response.data.errors);
        })
    }

    return oldAnime?(
       <TheForm submitHandler={submitHandler} buttonText={"UPDATE CHARACTER"} oldAnime={oldAnime} />
    ):null;
}

export default UpdateOne;
