import {useState} from "react";

const FormAdd = () =>{

    const [name, setName] =useState("");
    const [move, setMove] =useState("");
    const [skill, setSkill] =useState("");


    return (
    <div>
        <form>  
        <h2>CREATE AN ANIME CHARACTER</h2>
        <div>
          <label htmlFor="name">ENTER CHARACTER NAME:</label>
          <input type="text" id="name" name="name" onChange={(e)=>{setName(e.target.value)}}
          value={name}></input>
        </div>
        <div>
          <label htmlFor="move">ENTER FAVORITE MOVE:</label>
          <input type="text" id="move" name="move" onChange={(e)=>{console.log(e.target.value)}}
          value={move}></input>
        </div>
        <div>
          <label htmlFor="skill">ENTER SKILL LEVEL:</label>
          <input type="number" id="skill" name="skill" onChange={(e)=>{console.log(e.target.value)}}
          value={skill}></input>
        </div>
      </form>
        <hr/>
        <h2>DISPLAY VALUE</h2>
        <div>
            <label htmlFor="">CHARACTER NAME:{name}</label>
        </div>
        <div>
            <label htmlFor="">CHARACTER MOVE:</label>
        </div>
        <div>
            <label htmlFor="">CHARACTER SKILL:</label>
        </div>
      </div>
    );
}
export default FormAdd;
