const App=()=> {

  const [bgcolor,setBgColor]= useState("black");

  return (
    <div className="App">
      <div>
        <Formatter bgcolor={bgcolor} setBgColor={setBgColor}></Formatter>
        <FormAdd ></FormAdd>
      </div>
    </div>
  );
}

export default App;
