import axios from "axios";
import { useEffect, useState } from "react";
import { Link, Navigate, useNavigate } from "react-router-dom";
import HeaderNav from "./HeaderNav";



const ShowAll =(props)=>
{

    const navigate = useNavigate();
    const [characterCollection, setcharacterCollection] = useState([]);
    const [order,setOrder] = useState("http://localhost:8000/api/showAll");
    const [filter,setFilter]=useState("");

    useEffect(()=>{
        axios.get(order)
        .then((res)=>{
            // console.log(res);
            // console.log(res.data);
            setcharacterCollection(res.data);
        })
        .catch((err)=>console.log(err))
    },[])

    const deleteHandler =(idFromBelow)=>
    {
        console.log(idFromBelow);
        const check = window.confirm("Are you sure you want to delete this entry?");
        if(check)
        {
            axios.delete(`http://localhost:8000/api/deleteAnime/${idFromBelow}`)
            .then((res)=>{
                console.log(res.data);
                setcharacterCollection(characterCollection.filter(anime=>anime._id!==idFromBelow))
            })
            .catch((err)=>console.log(err))
        }
    }
    
    const updateHandler =(idFromBelow)=>{
        console.log(idFromBelow);
        navigate(`/edit/${idFromBelow}`);
    }

    const changeOrder =()=>{
        setOrder("http://localhost:8000/api/showAllByTitle");
    }
  

    const updateFilter =() =>{
        
           filter? setOrder("http://localhost:8000/api/search/"+filter):
           setOrder("http://localhost:8000/api/showAll");
        
       
        console.log(order);
    }

    return (
        <>
            <HeaderNav></HeaderNav>
            <br/>
            <button onClick={()=>{changeOrder()}}>ORDER BY TITLE</button>
            <br/>
            <input type="text" value={filter} onChange={(e)=>{setFilter(e.target.value)}}/>
            <button onClick={()=>{updateFilter()}}>Send Query</button>
            <hr/>
            {
                characterCollection && 
                characterCollection.map((element,index)=>{
                    return (
                        <div key={index}>
                            <p><Link to={`/showOne/${element._id}`}>SHOW MORE</Link></p>
                            <p>ANIME TITLE: {element.animeTitle}</p>
                            <p>CHARACTER NAME: {element.characterName}</p>
                            <p>POWER LEVEL: {element.powerLevel}</p>
                            <button onClick={()=>{deleteHandler(element._id)}}>DELETE</button>
                            <Link to={`/edit/${element._id}`} state={element}>UPDATE</Link>
                            
                        </div>
                    )
                })
            }
        </>
    )
}

export default ShowAll;
