    const {bgcolor,setBgColor} = props;

    const themeHolder={
        color:"Red",
        fontWeight:"bold"
    }

    const {anime,name,move,skill}=props.listv;
 

   
    console.log("From Display "+bgcolor);
    return(
        <div className="redText" >
        <h2>DISPLAY VALUE</h2>
        <div>
            <label htmlFor="">FROM: {anime}</label>
        </div>
        <div>
            <label htmlFor="">CHARACTER NAME:{name}</label>
        </div>
        <div>
            <label htmlFor="">CHARACTER MOVE:{move}</label>
        </div>
        <div>
            <label htmlFor="">CHARACTER SKILL:{skill}</label>
        </div>
        <button onClick={()=>{changeColor()}}>CLICK</button>
        </div>
      
    );
    
}
export default Display;
