import axios from 'axios';
import { useEffect, useState } from 'react';
import {Link, useNavigate} from 'react-router-dom';
const DisplayAll =(props)=>{

  const [pokemons, setPokemons] = useState([]);
  const {selectedType, setSelectedType} = props;

  useEffect(()=>{
    axios.get(selectedType)
    .then((res)=>{
        console.log(res.data);
        setPokemons(res.data.pokemon);
    })
    .catch((err)=>{console.log(err)})

  },[selectedType])

  const getId =(urx)=>{
      let ids;
         axios.get(urx)
               .then((res)=>{
                    ids= res.data.id;
                   console.log(ids);
                   console.log("The id is"+ids);
                   return ("https://pokeapi.co/api/v2/pokemon/"+ids);
               })
               .catch((err)=>{console.log(err)})
  }
    return(
        <>
          
            <p>THIS IS Display!</p>
           {pokemons &&
           pokemons.map((elem,index)=>{
               return (
               <div key={index}>
               {elem.pokemon.name}
               {getId(elem.pokemon.url)}
               <Link to={()=>{getId(elem.pokemon.url)}}>VIEW</Link>
               
               </div>
               )
           })}
           
        </>
    );
}
export default DisplayAll;
