import {useState} from "react";
const MyForm =()=>{

    const [name,setName]= useState("");
    const [skill,setSkill]= useState("");

    const [nameError, setNameError]=useState("");



    
    return(
        <div>
        <form>
            <h1>ADD FORM</h1>
            <h2>Current Value:{name}</h2>
            <div>
                <label htmlFor="inputName">Enter any name:</label>
                <input type="text" id="name" onChange={(e)=> setName(e.target.value)} 
                value={name}></input>
            </div>
            {(name.length >10 )? <h2>Should only be less than 10</h2>:null}
            <div>
                <label htmlFor="inputName">Enter any name:</label>
                <input type="text" id="name" onChange={(e)=> setName(e.target.value)} 
                value={name}></input>
            </div>
            <div>
                <label htmlFor="skillLevel">Enter any skill level:</label>
                <input type="number" id="skill" onChange={(e)=> setSkill(e.target.value)} 
                value={skill}></input>
            </div>
            {}
        </form>
        <div>
            <p>NAME:{name}</p>
            <p>skill:{skill}</p>
        </div>
        </div>
        
    )
}

export default MyForm;
