import logo from './logo.svg';
import './App.css';
import {useState} from "react";
import FormAdd from './components/FormAdd';
import Display from './components/Display';
import Formatter from './components/Formatter';



const App=()=> {

  const [bgcolor,setBgColor]= useState("");

  const listofPeople =[{
      surname:"MARCUS",
      firstname:"EDWARD",
      age:25
  },
  {
    surname:"MARCUS",
    firstname:"MICHAEL",
    age:12
},
  {
    surname:"PARKER",
    firstname:"PETER",
    age:14
}];

  const filteredList =listofPeople.filter((person)=>person.age>18);

  return (
    <div className="App">
      <div className={bgcolor}>
        <Formatter bgcolor={bgcolor} setBgColor={setBgColor}></Formatter>
        <FormAdd bgcolor={bgcolor} setBgColor={setBgColor}></FormAdd>
      </div>
    </div>
  );
}

export default App;
