import logo from './logo.svg';
import './App.css';
import FormAdd from './components/FormAdd';


const App=()=> {

  const [anime, setAnime] =useState([]);
  return (
    <div className="App">
      <FormAdd anime={anime} setAnime={setAnime}></FormAdd>
      {
          anime.map((element, index)=><Display key={index} listv={element}></Display>)
      }

    </div>
  );
}

export default App;
