import axios from 'axios';
import { useEffect, useState } from 'react';
import {Link, useNavigate} from 'react-router-dom';

const FormNew =(props)=>{

    const [poketypes,setPokeTypes] = useState([]);
    const [selectedTypex, setSelectedTypex]= props;

    useEffect(()=>{
        axios.get(`https://pokeapi.co/api/v2/type`)
        .then((res)=>{
            setPokeTypes(res.data.results);
        })
        .catch((err)=>{ console.log(err)})

    },[])

    const submitHandler=(e)=>{
        e.preventDefault();
        
    }
   
  
    return(
        <>
          
            <p>THIS IS FORM!</p>
            <form onSubmit={submitHandler}>
                <select name="selectType" onChange={(e)=>{setSelectedTypex(e.target.value)}}>
                <option value="" defaultValue>Select a Poke Type</option>
                {poketypes.map((elem,index)=>{
                    return(<option key={index} value={elem.url}>{elem.name}</option>)
                })}
                </select>
                <button>SUBMIT</button>
            </form>
           
        </>
    );
}
export default FormNew;
