import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

const TheForm =(props)=>{


    const {submitHandler, handleChange, anime, errors, buttonText}=props;

   
    return(
        <form onSubmit={(e)=>{submitHandler(e)}}>
        <div>
            <label>CHARACTER NAME:</label>
            <input type="text" value={anime.characterName} name="characterName" onChange={onChangeHandler}/>
            <br/>
            {errors.characterName && (<span> {errors.characterName.message} </span>)}
        </div>
        <div>
            <label>ANIME TITLE:</label>
            <input type="text" value={anime.animeTitle} name="animeTitle" onChange={onChangeHandler}/>
            <br/>
            {errors.animeTitle && (<span> {errors.animeTitle.message} </span>)}
        </div>
        <div>
            <label>POWER LEVEL:</label>
            <input type="number" value={anime.powerLevel}  name="powerLevel" onChange={onChangeHandler}/>
            <br/>
            {errors.powerLevel && (<span> {errors.powerLevel.message} </span>)}
        </div>
        <button type="submit">{props.buttonText}</button>
    </form>
    )
}

export default TheForm;
