import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import HeaderNav from "./HeaderNav";


const UpdateOne =(props)=>
{
    const navigate = useNavigate();
    const {aid} = useParams();

    const [editanime, seteditanime]= useState({
        characterName:'',
        animeTitle:'',
        powerLevel:0
    });
    const [errors, setErrors]=useState({});
    
    useEffect(()=>{
        axios.get("http://localhost:8000/api/showOne/"+aid)
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            seteditanime(res.data);
        })
        .catch((err)=>{
            console.log(err)
            setErrors(err.response.data.errors);
        })
    },[aid])

    const editsubmitHandler=(e)=>{
        e.preventDefault();
        axios.put("http://localhost:8000/api/updateAnime/"+aid,
            {
                characterName,
                animeTitle,
                powerLevel
            }
        )
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            navigate("/home/");
        })
        .catch((err)=>{
            console.log(err);
        })
    }

    return (
        <>
           <HeaderNav labeltxt={`RETURN HOME`} labeltxt2={`ADD NEW`} link2={`/createNew`} link={`/home`} headerText={`UPDATE ${characterName}`}/>
            <hr/>
           
        </>
    )
}

export default UpdateOne;
