import logo from './logo.svg';
import './App.css';
import React,{useState, useEffect} from "react";
import axios from 'axios';  
import ShowElements from './components/ShowElements';





const App=()=> {

  const [starship, setStarship] = useState({});
  const [activeship, setActiveShip] = useState(1);
  // useEffect(()=>{
  //   fetch("https://swapi.dev/api/starships/9")
  //   .then((res)=>{
  //     console.log(res);
  //     return res.json();
  //   })
  //   .then((res)=>{
  //     console.log(res);
  //     setStarship(res);
  //   })
  //   .catch((err)=>{console.log(err)})
  // },[])

  useEffect(()=>{
    axios.get(`https://swapi.dev/api/starships/${activeship}`)
    .then((res)=>{

      console.log(res.data);
      setStarship(res.data);
    })
    .catch((err)=>{

      console.log(err)})
  },[activeship])

  const nextItem =()=>{
      setActiveShip(activeship+1);
  }
  const prevItem =()=>{
    setActiveShip(activeship-1);
}
  return (
    <div className="App">
      <ShowElements dets={starship}></ShowElements>
      <button onClick={()=>{prevItem()}}>PREVIOUS</button>
      <button onClick={()=>{nextItem()}}>NEXT</button>
    </div>
  );
}

export default App;
