import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";



const ShowAll =(props)=>
{

    const [characterCollection, setcharacterCollection] = useState([]);

    useEffect(()=>{
        axios.get("http://localhost:8000/api/showAll")
        .then((res)=>{
            // console.log(res);
            // console.log(res.data);
            setcharacterCollection(res.data);
        })
        .catch((err)=>console.log(err))
    })

    const deleteHandler =(elementId)=>
    {
        axios.delete(`/api/deleteAnime/${elementId}`)
        .then((res)=>{
            console.log(res.data);
            setcharacterCollection(characterCollection.filter((characterx)=> characterx))
        })
        .catch((err)=>console.log(err))
    }

    return (
        <>
            <h1>ANIME CHARACTER LIST</h1>
            <hr/>
            <Link to="/createNew/">CREATE NEW CHARACTER</Link>
            {
                characterCollection && 
                characterCollection.map((element,index)=>{
                    return (
                        <div key={index}>
                            <p><Link to={`/showOne/${element._id}`}>SHOW MORE</Link></p>
                            <p>ANIME TITLE: {element.animeTitle}</p>
                            <p>CHARACTER NAME: {element.characterName}</p>
                            <p>POWER LEVEL: {element.powerLevel}</p>
                            <button>EDIT</button>
                            <button onClick={()=>{deleteHandler(element._id)}}>DELETE</button>
                        </div>
                    )
                })
            }
        </>
    )
}

export default ShowAll;
