import { useState } from "react";

const Display =(props)=>{

    const {anime,setAnime} = props;
    console.log(anime);
    const deleteAnime =(idFromBelow)=>{
       setAnime(anime.filter((element,index)=>element.id!==idFromBelow)) ;
    }
    return(
    <div>
      {anime.map((element,index)=>(
        <div key={index}>
        <h2>DISPLAY VALUE</h2>
        <div >
            <label htmlFor="">FROM: {element.animetitle}</label>
        </div>
        <div>
            <label htmlFor="">CHARACTER NAME:{element.name}</label>
        </div>
        <div>
            <label htmlFor="">CHARACTER MOVE:{element.move}</label>
        </div>
        <div>
            <label htmlFor="">CHARACTER SKILL:{element.skill}</label>
        </div>
       
        </div>
      ))}
    </div>


   
    );
    
}
export default Display;
