import logo from './logo.svg';
import './App.css';
import React, {useState, useEffect} from 'react';



const App=()=> {

  const [character, setCharacter] = useState({});
  const {name,height,gender} = character;

  useEffect(()=>{
    fetch('https://swapi.dev/api/people/4').then((res)=>{
      return res.json();
    })
    .then((res)=>
    { 
      console.log(res);
      setCharacter(res);
    })
    .catch((err)=>{
      console.log(err);
    })
  },[])


  return (
    <div className="App">
      <p>NAME:{name}</p>
      <p>HEIGHT:{height}</p>
      <p>GENDER:{gender}</p>
    </div>
  );
}

export default App;
