import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";


const NewAnime =(props)=>
{
    const navigate = useNavigate();
    const [characterName,setcharacterName]= useState("");
    const [animeTitle,setanimeTitle]= useState("");
    const [powerLevel,setpowerLevel]= useState(0);

    const [errors, setErrors]=useState({});

    const submitHandler=(e)=>{
        e.preventDefault();
        axios.post("http://localhost:8000/api/newAnime",
            {
                characterName,
                animeTitle,
                powerLevel
            }
        )
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            navigate("/home/");
        })
        .catch((err)=>{
            console.log(err.response.data.message);
            setErrors(err.response.data);
        })
    }

    return (
        <>
            <h1>CREATE NEW CHARACTER</h1>
            <hr/>
            <Link to="/home/">SHOW ALL CHARACTERS</Link>
            <form onSubmit={(e)=>{submitHandler(e)}}>
                <div>
                    <label>CHARACTER NAME:</label>
                    <input type="text" value={characterName} onChange={(e)=>{setcharacterName(e.target.value)}}/>
                    {errors.characterName && (<span> {} </span>)}
                </div>
                <div>
                    <label>ANIME TITLE:</label>
                    <input type="text" value={animeTitle} onChange={(e)=>{setanimeTitle(e.target.value)}}/>
                </div>
                <div>
                    <label>POWER LEVEL:</label>
                    <input type="number" value={powerLevel} onChange={(e)=>{setpowerLevel(e.target.valueAsNumber)}}/>
                </div>
                <button type="submit">ADD NEW CHARACTER</button>
            </form>
        </>
    )
}

export default NewAnime;
