import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import HeaderNav from "./HeaderNav";
import TheForm2 from "./theForm2";


const NewAnime =(props)=>
{
    const navigate = useNavigate();


    const [newanime, setnewAnime]= useState({
        characterName:'',
        animeTitle:'',
        powerLevel:0
    });
    const [errors, setErrors]=useState({});

    const newsubmitHandler=(e)=>{
        e.preventDefault();
        axios.post("http://localhost:8000/api/newAnime",newanime)
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            navigate("/home/");
        })
        .catch((err)=>{
            //console.log(err.response.data.message);
            console.log(err.response.data.errors);
            setErrors(err.response.data.errors);
        })
    }

    const newchangeHandler=(e)=>
    {
        const newAnime = {...newanime};
        newAnime[e.target.name] = e.target.value;
        setnewAnime(newAnime);
    }

    return (
        <>
            <HeaderNav labeltxt={`RETURN HOME`} link={`/home`} headerText={`ADD NEW CHARACTER`}/>
            <hr/>
            <TheForm2
            submitHandler={newsubmitHandler}
            changeHandler={newchangeHandler}
            errors={errors}

             />
        </>
    )
}

export default NewAnime;
