import axios from 'axios';
import React, {useState, useEffect} from 'react';
import DisplayAll from './DisplayAll';

const Loader =(props)=>{

    const [typeList, setTypeList] = useState([]);
    const [selectedType, setSelectedType] =useState("");
    useEffect(()=>{
        axios.get(`https://pokeapi.co/api/v2/type`)
        .then((res)=>
        { 
           setTypeList(res.data.results);
           //console.log(res.data.count);
        })
        .catch((err)=>{
          console.log(err);
        })
      },[typeList])
    
      const submitHandler=(e)=>{
        e.preventDefault();
        console.log(e.target[0].value);
        setSelectedType(e.target[0].value);
      }
 return(
     <>
        <h1>SELECT POKEMON TYPE:</h1>
        <form onSubmit={(e)=>{submitHandler(e)}}>
            <select name="TypeList" >
                <option value="">--SELECT HERE--</option>
                {
                    typeList.map((elem,index)=>{
                        return (<option key={index} value={elem.url}>{elem.name}</option>)
                    })
                } 
            </select>
            <button>LOAD POKEMONS</button>
        </form>
        <hr/>
        <h1>LIST OF POKEMONS</h1>
                {/* {selectedType&& */}
                    <DisplayAll selectedType={selectedType}  setSelectedType={setSelectedType}/>
                }
        
        
     </>
        )
}
export default Loader;
