import Weird from "./Weird";
import {useState} from "react";

const RegS=(props)=>{

  console.log("REGS IS RENDERING!");
   //const {anime} = props;

   //Prop Destructuring
   const {name,skill,level} = props.anime;
   //State
   const [nlevel, setnlevel] = useState(level);
   //const {key} =props;

   const alertx=()=>{
    setnlevel(nlevel+1);
   }

   const levelCheck =()=>{
      if(nlevel>=30)
      {
        return (<p>This character is super strong</p>);
      }
      else if(nlevel>=20)
      {
        return (<p>This character is slightly strong</p>);
      }
      else
      {
        return (<p>This character is still Weak</p>);
      }
   }


    return (
      <div>
        <p>Name:{name}</p>
        <p>Skill:{skill}</p>
        <p>Level:{nlevel}</p>
        <button onClick={alertx}>LEVEL ME UP!</button> 
        {levelCheck()}
        {(nlevel>=30)}
      </div>
      );
}
export default RegS;
