import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";


const ShowOne =(props)=>
{

    const [character, setCharacter] = useState({});
   
    const {aid} = useParams();

    const navigate = useNavigate();
    
    useEffect(()=>{
        axios.get("http://localhost:8000/api/showOne/"+aid)
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            setCharacter(res.data);
        })
        .catch((err)=>console.log(err))
    })

    

    const deleteHandler =(idFromBelow)=>
    {
        console.log(idFromBelow);
        const check = window.confirm("Are you sure you want to delete this entry?");
        if(check)
        {
            axios.delete(`http://localhost:8000/api/deleteAnime/${idFromBelow}`)
            .then((res)=>{
                console.log(res.data);
                navigate("/");
            })
            .catch((err)=>console.log(err))
        }
    }

    return (
        <>
            {character && 
            <><h1>ANIME TITLE: {character.animeTitle}</h1>
            <p>CHARACTER NAME: {character.characterName}</p>
            <p>POWER LEVEL: {character.powerLevel}</p>
            <button onClick={()=>{deleteHandler(aid)}}>DELETE</button>
            </>
            }
            
        </>
    )
}

export default ShowOne;
