import { useState } from "react";


const Formatter=(props)=>{

    const {bgcolor,setBgColor} = props;
    
    const changeColor =(changeColorB)=>{
        if(changeColorB==0)
        {
            setBgColor("RED");
        }
        else if(changeColorB==1)
        {
            setBgColor("BLUE");
        }
        else if(changeColorB==1){
            setBgColor("YELLOW");
        }
    }
    return(
        <>
            <div>
                <h1>CHANGE BACKGROUND</h1>
                <h1>Current Color:{bgcolor}</h1>
                <input type="radio" name="color" onClick={()=>{changeColor(0)}}/>
                <label>RED</label>
                <input type="radio" name="color" onClick={()=>{changeColor(1)}}/>
                <label>BLUE</label>
                <input type="radio" name="color" onClick={()=>{changeColor(2)}}/>
                <label>YELLOW</label>
                
            </div>
        </>
    )
}
export default Formatter;
