import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Link, useParams } from "react-router-dom";


const UpdateOne =(props)=>
{
    const navigate = useNavigate();
    
    const {aid} = useParams();
    const [characterName,setcharacterName]= useState("");
    const [animeTitle,setanimeTitle]= useState("");
    const [powerLevel,setpowerLevel]= useState(0);
    

    useEffect(()=>{
        axios.get("http://localhost:8000/api/showOne/"+aid)
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            console.log(res.data.characterName);

            setcharacterName(res.data.characterName);
            setanimeTitle(res.data.animeTitle);
            setpowerLevel(res.data.powerLevel);
        })
        .catch((err)=>console.log(err))
    },[aid])
   




    const submitHandler=(e)=>{
        e.preventDefault();
        let check = window.confirm("Are you sure you want to update?");
        if(check)
        {
        axios.put("http://localhost:8000/api/updateAnime/"+aid,
            {
                characterName,
                animeTitle,
                powerLevel
            }
        )
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            navigate("/home");
        })
        .catch((err)=>{console.log(err)})
         }
    }

    return (
        <>
            <h1>CREATE NEW CHARACTER</h1>
            <hr/>
            <Link to="/home/">SHOW ALL CHARACTERS</Link>
            <form onSubmit={(e)=>{submitHandler(e)}}>
                <div>
                    <label>CHARACTER NAME:</label>
                    <input type="text" value={characterName} onChange={(e)=>{setcharacterName(e.target.value)}}/>
                </div>
                <div>
                    <label>ANIME TITLE:</label>
                    <input type="text" value={animeTitle} onChange={(e)=>{setanimeTitle(e.target.value)}}/>
                </div>
                <div>
                    <label>POWER LEVEL:</label>
                    <input type="number" value={powerLevel} onChange={(e)=>{setpowerLevel(e.target.valueAsNumber)}}/>
                </div>
                <button type="submit">Update Character</button>
            </form>
        </>
    )
}

export default UpdateOne;
