import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import HeaderNav from "./HeaderNav";
import TheForm2 from "./TheForm2";


const UpdateOne =(props)=>
{
    const navigate = useNavigate();
    const {aid} = useParams();

    const [editanime, seteditanime]= useState({
        characterName:'',
        animeTitle:'',
        powerLevel:0
    });
    const [disp,setDisp]=useState();
    const [errors, setErrors]=useState({});
    
    useEffect(()=>{
        axios.get("http://localhost:8000/api/showOne/"+aid)
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            seteditanime(res.data);
        })
        .catch((err)=>{
            console.log(err)
            setErrors(err.response.data.errors);
        })
    },[aid])

    const editsubmitHandler=(e)=>{
        e.preventDefault();
        axios.put("http://localhost:8000/api/updateAnime/"+aid,editanime)
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            navigate("/home/");
        })
        .catch((err)=>{
            console.log(err);
        })
    }

    const editchangeHandler=(e)=>
    {
        const editAnime2 = {...editanime};
        editAnime2[e.target.name] = e.target.value;
        seteditanime(editAnime2);
    }

    return (
        <>
           <HeaderNav labeltxt={`RETURN HOME`} labeltxt2={`ADD NEW`} link2={`/createNew`} link={`/home`} headerText={`UPDATE ${editanime.characterName}`}/>
            <hr/>
            <TheForm2
            submitHandler={editsubmitHandler}
            changeHandler={editchangeHandler}
            errors={errors}
            anime={editanime}
            labeltxt ={`UPDATE CHARACTER`}/>
        </>
    )
}

export default UpdateOne;
