import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import TheForm from "./TheForm";


const NewAnime =(props)=>
{
    const navigate = useNavigate();
    const [anime, setNewAnime]= useState({
        characterName:'',
        animeTitle:'',
        powerLevel:0
    })

    const [characterName,setcharacterName]= useState("");
    const [animeTitle,setanimeTitle]= useState("");
    const [powerLevel,setpowerLevel]= useState(0);

    const [errors, setErrors]=useState({});

    const submitHandler=(e)=>{
        e.preventDefault();
        axios.post("http://localhost:8000/api/newAnime",
            {
                characterName,
                animeTitle,
                powerLevel
            }
        )
        .then((res)=>{
            console.log(res);
            console.log(res.data);
            navigate("/home/");
        })
        .catch((err)=>{
            //console.log(err.response.data.message);
            console.log(err.response.data.errors);
            setErrors(err.response.data.errors);
            setNewAnime()
        })
    }

    const onChangeHandler =(e)=>{
        const newStateanime = {...anime};
        newStateanime[e.target.name]= e.target.value;
        setNewAnime(newStateanime);
    }

    return (
       <TheForm submitHandler={submitHandler} onChangeHandler={onChangeHandler} anime={anime} errors/>
    )
}

export default NewAnime;
