import logo from './logo.svg';
import './App.css';
import React, {useState, useEffect} from 'react';
import Characters from './components/Characters';
Characters


const App=()=> {

  const [character, setCharacter] = useState({});
  const {name,height,gender} = character;
  const [active, setActive] = useState(1);

  useEffect(()=>{
    fetch("https://swapi.dev/api/people/"+active)
    .then((res)=>{
      return res.json();
    })
    .then((res)=>
    { 
      console.log(res);
      setCharacter(res);
    })
    .catch((err)=>{
      console.log(err);
    })
  },[active])

 const incre=()=>{
   setActive(active+1);
 }
 const decre=()=>{
  setActive(active-1);
}

  return (
    <div className="App">

      <button onClick={()=>{decre()}}>PREVIOUS</button>
      <button onClick={()=>{incre()}}>NEXT</button>
    </div>
  );
}

export default App;
