import {useState} from "react";
const MyForm =()=>{

    const [animegroup,setAnimeGroup]= useState([]);

    const [name,setName]= useState("");
    const [skill,setSkill]= useState("");
    const [anime,setAnime]= useState("");


    const [nameError, setNameError]=useState("");


    const animeNamehandler=(e)=>
    {
        if(e.target.value.length < 10)
        {
            setNameError("Please have more than 10 characters!");
        }
        else{
            setNameError("");
        }
        setAnime(e.target.value);
    }

    
    return(
        <div>
        <form>
            <h1>ADD FORM</h1>
            <h2>Current Value:{name}</h2>
            <div>
                <label htmlFor="inputName">Enter any name:</label>
                <input type="text" id="name" onChange={(e)=> setName(e.target.value)} 
                value={name}></input>
            </div>
            {(name.length >10 )? <h2>Should only be less than 10</h2>:null}
            <div>
                <label htmlFor="animeName">Enter Anime name:</label>
                <input type="text" id="name" onChange={animeNamehandler} 
                value={anime}></input>
            </div>
            {nameError && <p>{nameError}</p>}
            <div>
                <label htmlFor="skillLevel">Enter any skill level:</label>
                <input type="number" id="skill" onChange={(e)=> setSkill(e.target.value)} 
                value={skill}></input>
            </div>
            {}
            <button type="submit">SEND</button>
        </form>
        <div>
            <p>NAME:{name}</p>
            <p>ANIME:{anime}</p>
            <p>skill:{skill}</p>
        </div>
        </div>
        
    )
}

export default MyForm;
